mapper.select:
		select
			usu.id,
			usu.tipo,
			usu.cpf,
			usu.nome,
			date_format(usu.dta_nascimento, '%d/%m/%Y') as dta_nascimento,
			usu.email,
			usu.ddd,
			usu.telefone,
			usu.genero,
			usu.senha,
			usu.endereco,
			end.id as id_endereco,
			end.logradouro,
			end.numero,
			end.complemento,
			end.bairro,
			end.cidade,
			end.uf,
			end.cep
		from usuario usu
		left join endereco end
			on usu.endereco = end.id
        left join funcionario_restaurante fr
        		on fr.id_funcionario = usu.id

			where


					usu.id = #{valor}


					usu.email = #{valor}


					fr.cnpj_restaurante = #{valor}

mapper.insert:
INSERT INTO usuario ( tipo, cpf, nome, dta_nascimento, email, ddd, telefone, genero, senha
,endereco
) VALUES ( #{tipo}, #{cpf}, upper(#{nome}), str_to_date(#{dta_nascimento}, '%d/%m/%Y'), lower(#{email}), #{ddd}, #{telefone}, #{genero}, #{senha}
,(select max(id) from endereco)
)
